import React, { Component } from 'react'
import { Letter } from "./Letter";

export class Solution extends Component {

    updateRiddle = () => {
        this.updateRiddle(this.props.solution)
    }

    showLetter = (letter) => {
        this.props.showLetter(letter)
    }

    render() {

        const numOfLetters = this.props.solution.answer.length
        const underScores = []
        for (let i=0; i<numOfLetters; i++){
          underScores.push('_ ')
        }

        return (
            <div>
                <div class='solution'>
                    <div id='hint'>
                        {this.props.solution.hint}
                    </div>
                    <div id='answer'>
                        {underScores.map( underScore => underScore)}
                        <br></br>
                        <Letter answer={this.props.solution.answer.split('')} letterStatus={this.props.letterStatus}  />
                    </div>
                </div>
            </div>
        )
    }

}
