import React, { Component } from 'react'
import { Letter } from "./Letter";
import './App.css';

export class Letters extends Component {

    render(){
        const letterStatus = Object.entries(this.props.letterStatus)
        const chosenLettersMatrix = letterStatus.filter( letter => letter[1] == true )
        const chosenLetters = chosenLettersMatrix.map( matrix => matrix[0])
        const availableLettersMatrix = letterStatus.filter( letter => letter[1] == false)
        const availableLetters = availableLettersMatrix.map( matrix => matrix[0])

        return (
            <div>
                <Letter availableLetters={availableLetters} chosenLetters={chosenLetters}/>
            </div>
        )
    }
}
