import React, { Component } from 'react'
// import { Letter } from "./Letter";


export class EndGame extends Component {

    render(){

        const answerLetters = this.props.answer.split('')
        const trues = Object.values(this.props.letterStatus).filter( value => value == true )
        console.log(trues)

        return (
            trues.length == answerLetters.length 
                ? <div>Congratulations!</div> 
                : this.props.counter == 0 || this.props.counter < answerLetters.length
                    ? null
                    : <div>The answer is: {this.props.answer}</div>
        )
    }
}
