import React, { Component } from 'react'
import { Letter } from "./Letter";
import './App.css';

export class Letters extends Component {

    render(){

        const letterStatus = Object.entries(this.props.letterStatus)
        const chosenLetters = letterStatus.filter( letter => letter[1] == true )
        const availableLetters = Object.values(letterStatus.filter( letter => letter[1] == false))
        console.log('availableLetters', availableLetters)

        return (
            <div>
                <Letter availableLetters={availableLetters} chosenLetters={chosenLetters}/>
            </div>
        )
    }
}
