import React, { Component } from 'react'
import { Letter } from "./Letter";


export class Solution extends Component {

    constructor(){
        super()
    }

    render(){

        let wordBreakDown = this.props.solution.word.split('')

        let letters = Object.keys(this.props.letterStatus)

        for (let i=0; i<letters.length; i++){
            if(this.props.letterStatus[letters[i]] == 'false'){
                return(
                    <div class='answer'>
                        <Letter letter={'_'}/>
                    </div>
                )
            }
            else {
                return(
                    <div class='answer'>
                    {wordBreakDown.map( letter => <Letter letter={letter}/>)}
                    </div>
                )
            }
        }

        return(
            <div>

                <div class='hint'>
                    {this.props.solution.hint}
                </div>
            </div>
        )
    }

}


