import React, { Component } from 'react';
import logo from './logo.svg';
import { Solution } from './Solution.js';
import { Score } from './Score.js';
import { Letters } from './Letters.js';
import './App.css';

class App extends Component {

  constructor(){
    super()
    this.state = {
      letterStatus: this._generateLetterStatuses(),
      riddleShown: null,
      solutions: [
        {
          hint: 'Vicki\'s favorite food',
          answer: 'Bamba'
        },
        {
          hint: 'Best music genre is...?',
          answer: 'Metal'
        },
        {
          hint: 'Best OS is...? (hint: not windows)',
          answer: 'Linux'
        },
        {
          hint: 'blabla',
          answer: 'blablablabalbalblabla'
        }
      ],
      score: 0
    }
  }

  _selectLetter = (letter) => {
    let newLetterStatus = {...this.state.letterStatus}
    newLetterStatus[letter] = true
    this.setState({
      letterStatus: newLetterStatus
    })
  }

  _generateLetterStatuses(){
    let alphabet = {}
    for (let i=0; i<26; i++){
        alphabet[((i+10).toString(36))] = false
    }
    return alphabet
  }

  _chooseRandomRiddle = () => {

    const randNum = Math.floor(Math.random() * this.state.solutions.length)
    let copiedSolutions = [...this.state.solutions]
    let chosenRiddle = copiedSolutions[randNum]
    if (this.state.riddleShown == null){
      this.setState({ riddleShown: chosenRiddle })
    }
    // !this.state.riddleShown 
    //   ? this.setState({ riddleShown: chosenRiddle })
    //   : console.log('sdsd')
    // return this.state.riddleShown
  }

  render () {

    _chooseRandomRiddle()
    console.log('blablabal', this.state.riddleShown)

    return (
      <div>
        <Score score={this.state.score}/>
        <Letters letterStatus={this.state.letterStatus} selectLetter={this._selectLetter} />
        <Solution letterStatus={this.state.letterStatus} solution={this.state.riddleShown}/>
      </div>
    )
  }
}

export default App;
