import React, { Component } from 'react';
import './App.css';

export class Letter extends Component {

    render() {

        return(
            this.props.answer 
                ? <span> {this.props.answer.map( letter => letter)} {this.props.chosenLetters.map( chosenLetter => chosenLetter )} </span> 
                : <span> {this.props.chosenLetters}</span>
        )
    }
}
