// import Letter from "./Letter";
import React, { Component } from 'react'

export class Solution extends Component {

    constructor(numOfLetters){
        super()
        this.numOfLetters = 5
    }

    render() {

        const _chooseRandomHint = function(hintsArr){
            const randNum = Math.floor(Math.random() * hintsArr.length)  
            return hintsArr[randNum]
        }

        const underScores = []
        for (let i=0; i<this.numOfLetters; i++){
          underScores.push('_ ')
        }

        return(
            <div>
                <div class='solution'>
                    <div id='hint'>
                        {_chooseRandomHint(this.props.solution.hints)}
                    </div>
                    <div id='answer'>
                        {underScores.map( underScore => underScore)}
                    </div>

                </div>
            </div>
        )
    }

}
