// import Letter from "./Letter";
import React, { Component } from 'react'

export class Solution extends Component {

    constructor(numOfLetters,hint){
        super()
        this.numOfLetters = 5
    }

    render() {
      const underScores = []
      for (let i=0; i<this.numOfLetters; i++){
        underScores.push('_ ')
      }

      return(
          <div>
              <div class='solution'>
                  <div id='hint'>
                      {this._chooseRandomHint()}
                  </div>
                  <div id='answer'>
                      {underScores.map( underScore => underScore)}
                  </div>
              </div>
          </div>
      )
    }

}
