import React, { Component } from 'react';
import './App.css';

export class Letter extends Component {

    selectLetter = () => {
        this.props.selectLetter(this.props.availableLetters)
        this.props.showLetter(this.props.chosenLetters)
    }

    render() {
        return (
            <span>
                <span id='available-letters' onClick={this.selectLetter}>
                    {this.props.availableLetters}
                </span>
                {/* <span id='chosenLetters'>
                    {this.props.chosenLetters}
                </span> */}
                <span id='answer'>
                    {/* {this.props.answer 
                        ? <span> {this.props.answer.map( letter => letter)} {this.props.chosenLetters} </span> 
                        : <span> {this.props.chosenLetters}</span> } */}
                        {this.props.answer}
                </span>
            </span>
        )
    }
}
