import React, { Component } from 'react';
import './App.css';

export class Letter extends Component {

    render() {
        return (
            <div>
                <div id='answer'>
                    {/* {this.props.answer 
                        ? <span> {this.props.answer.map( letter => letter)} {this.props.chosenLetters} </span> 
                        : <span> {this.props.chosenLetters}</span> } */}
                        {this.props.answer}
                </div>
                <div id='available-letters'>
                    {this.props.availableLetters.map( letter => <span>{letter}</span> )}
                </div>
            </div>
        )
    }
}
