import React, { Component } from 'react'
// import { Letter } from "./Letter";


export class EndGame extends Component {

    render(){

        const answerLetters = this.props.answer.split('').filter( letter => letter!= '_ ')
        const trues = Object.values(this.props.letterStatus).filter( value => value == true )

        return (
            answerLetters.length == trues.length ? <div>Congratulations!</div> : <div>The answer is: {this.props.answer}</div>
        )
    }
}
