import React, { Component } from 'react';
import './App.css';

export class Letter extends Component {

    render() {
        // console.log(this.props.answer)
        const numOfLetters = 0
        this.props.answer ? this.props.answer.length : null

        const underScores = []
        for (let i=0; i<numOfLetters; i++){
          underScores.push('_ ')
        }

        return (
            <div>
                <div id='answer'>
                    {underScores.map(underScore => underScore)}
                    {/* {this.props.answer 
                        ? <span> {this.props.answer.map( letter => letter)} {this.props.chosenLetters} </span> 
                        : <span> {this.props.chosenLetters}</span> } */}
                        {this.props.answer}
                </div>
                <div id='available-letters'>
                    {this.props.availableLetters}
                </div>
            </div>
        )
    }
}
