import React, { Component } from 'react';
import logo from './logo.svg';
import { Solution } from './Solution.js';
import { Score } from './Score.js';
import { Letters } from './Letters.js';
import './App.css';

class App extends Component {

  constructor(){
    super()
    this.state = {
      letterStatus: this._generateLetterStatuses(),
      solution: {
        word: '',
        hints: [
          'Vicki\'s favorite food',
          'Best music genre is...?',
          'Best OS is...? (hint: not windows)',
        ]
      }
    }
  }

  _generateLetterStatuses(){
    let alphabet = {}
    for (let i=0; i<26; i++){
        alphabet[((i+10).toString(36))] = false
    }
    return alphabet
  }

  render () {

    return(
      <div>
        <Solution letterStatus={this.letterStatus} solution={this.state.hints}/>
        <Score />
        <Letters letterStatus={this.letterStatus}/>
      </div>
    )

  }

}

export default App;
