import React, { Component } from 'react';
import logo from './logo.svg';
import { Solution } from './Solution.js';
import { Score } from './Score.js';
import { Letters } from './Letters.js';
import { Start } from './Start.js';
import './App.css';



class App extends Component {

  constructor(){
    super()
    this.state = {
      letterStatus: this._generateLetterStatuses(),
      solution: {
        word: 'Pikachu',
        hint: 'Pokemon?'
      },
      score: 0
    }
  }

  _generateLetterStatuses(){
    let alphabet = {}
    for (let i=0; i<26; i++){
        alphabet[((i+10).toString(36))] = false
    }
    return alphabet
  }

  selectLetter = (letter) => {
    let localLetterStatus = {...this.state.letterStatus}
    localLetterStatus[letter] = true
    this.setState({
      letterStatus: localLetterStatus
    })
  }

  updateScore = () => {
    let currentScore = this.state.score
    this.setState({

    })
  }

  render(){
    return(
      <div>
        <Letters letterStatus={this.state.letterStatus} selectLetter={this.selectLetter}/>
        <Score score={this.state.score}/>
        <Solution letterStatus={this.state.letterStatus} solution={this.state.solution} updateScore={this.updateScore}/>
      </div>
    )
  }

}

export default App;

