import React, { Component } from 'react'
import { Letter } from "./Letter";
import './App.css';

export class Letters extends Component {

    selectLetter = (letter) => {
        this.props.selectLetter(letter)
    }

    showLetter = (letter,index) => {
        let solution = ['h', 'df']
        if (letter) {
        }
        else {
            const numOfLetters = this.props.solution.answer.length
            const solution = []
            for (let i=0; i<numOfLetters; i++){
              solution.push('_ ')
            }
        }
        this.props.updateAnswer(solution)
    }

    render(){

        const letterStatus = Object.entries(this.props.letterStatus)
        const chosenLettersMatrix = letterStatus.filter( letter => letter[1] == true )
        const chosenLetters = chosenLettersMatrix.map( matrix => matrix[0])
        const availableLettersMatrix = letterStatus.filter( letter => letter[1] == false)
        const availableLetters = availableLettersMatrix.map( matrix => matrix[0])

        return (
            <div>
                Available Letters: <br></br>
                {availableLetters.map( letter => <Letter availableLetters={letter} selectLetter={this.selectLetter}/> )}
                {chosenLetters.map( (letter,i) => <Letter chosenLetters={letter,i} /> )}
            </div>
        )
    }
}
