import React, { Component } from 'react'
import { Letter } from "./Letter";

export class Solution extends Component {

    constructor(numOfLetters){
        super()
        this.numOfLetters = 5
    }

    render() {


        const underScores = []
        for (let i=0; i<this.numOfLetters; i++){
          underScores.push('_ ')
        }

        return(
            <div>
                <div class='solution'>
                    <div id='hint'>
                        {this.props.solution.hint}
                    </div>
                    <div id='answer'>
                        {underScores.map( underScore => underScore)}
                    </div>
                    <Letter word={this.props.solution.word.split('')} letterStatus2={this.props.letterStatus} />
                </div>
            </div>
        )
    }

}
