// import Letter from "./Letter";
import React, { Component } from 'react'

export class Solution extends Component {

    constructor(numOfLetters,hint){
        super()
        this.numOfLetters = 5
        this.hints = [
            'Vicki\'s favorite food',
            'Best music genre is...?',

        ]
      }

      _chooseRandomHint(){
        const randNum = Math.floor(Math.random() * this.hints.length)  
        return this.hints[randNum]
      }

      render() {
        const underScores = []
        for (let i=0; i<this.numOfLetters; i++){
          underScores.push('_ ')
        }

        return(
            <div class='solution'>
                <div id='hint'>
                    {this._chooseRandomHint()}
                </div>
                <div id='answer'>
                    {underScores.map( underScore => underScore)}
                </div>
            </div>
        )
      }

}
