import React, { Component } from 'react'

export class Solution extends Component {

    constructor(numOfLetters,hint){
        super()
        this.numOfLetters = 5
        this.hints = [
            'Vicki\'s favorite food',
            'Best music genre is...?',
            'Best OS is...? (hint: not windows)',
        ]
        this.state = {
            letterStatus: {
                A: false,
                B: false,
                C: false,
                D: false,
                E: false,
                F: false,
                G: false,
                H: false,
                I: false,
                J: false,
